import React, { useState } from 'react'


export const AddTodo = (addTodo) => {

    const[title, setTitle]= useState("");
    const[desc, setDesc] = useState("");

    const submit = (e) => {
        e.preventDefault();
        if(!title || !desc){
            alert("Title or Description cannot be blank")
        }
        addTodo(title, desc);
    }
    return (
        <div className='container'>
            <h3>Add a Todo</h3>
            <htmlForm onSubmit={submit}>
                <div className="mb-3">
                    <label htmlFor="title" className="htmlForm-label">Toto Title</label>
                    <input type="text"  value={title} onChange={(e) => {setTitle(e.target.value)}} 
                    className="htmlForm-control" id="title"/>
                </div>

                <div className="mb-3">
                    <label htmlFor="desc" className="htmlForm-label">Todo Description</label>
                    <input type="text" value={desc} onChange={(e) => {setDesc(e.target.value)}} 
                    className="htmlForm-control" id="desc"/>
                </div>
                <button type="submit" className="btn btn-sm btn-success">Add Todo</button>
            </htmlForm>
        </div>
    )
}
